mapper.delete:
delete from TBL013
where PN_MNT_RT_NO = #{pnMntRtNo}
and BN_NO = (SELECT cf_get_bn_no(#{bnMbrId}))

mapper.select:
SELECT ( SELECT bb1.SELL_YN FROM ( SELECT * FROM `TBL007-A` UNION ALL SELECT * FROM `TBL007-B` UNION ALL SELECT * FROM `TBL007-C` ) bb1 WHERE a.PN_MDL_NO = bb1.PN_MDL_NO AND a.PN_STOR = bb1.PN_STOR AND bb1.SELL_YN = 'y' AND bb1.BN_NO = (SELECT cf_get_bn_no(#{bnMbrId})) AND bb1.MNT_CARR = #{mntCarr} GROUP BY bb1.SELL_YN ) AS 'sellStatus', DATE_FORMAT(( SELECT MAX(AMD_DT) FROM ( SELECT * FROM `TBL007-A` UNION ALL SELECT * FROM `TBL007-B` UNION ALL SELECT * FROM `TBL007-C` ) bb2 WHERE a.PN_MDL_NO = bb2.PN_MDL_NO AND a.PN_STOR = bb2.PN_STOR AND bb2.BN_NO = (SELECT cf_get_bn_no(#{bnMbrId})) AND bb2.MNT_CARR = #{mntCarr} ), '%Y-%m-%d %H:%i') as 'lastModiDate', ( SELECT bb3.AMD_MBR_ID FROM ( SELECT * FROM `TBL007-A` UNION ALL SELECT * FROM `TBL007-B` UNION ALL SELECT * FROM `TBL007-C` ) bb3 WHERE a.PN_MDL_NO = bb3.PN_MDL_NO AND a.PN_STOR = bb3.PN_STOR AND bb3.BN_NO = (SELECT cf_get_bn_no(#{bnMbrId})) AND bb3.MNT_CARR = #{mntCarr} ORDER BY bb3.AMD_DT DESC LIMIT 0, 1 ) AS 'lastModiMbr', a.PN_MKR, a.PN_MDL_NM, a.PN_MSRP, a.PN_NET_TYPE, a.PN_STOR, a.PN_MDL_NO, a.REL_DT FROM `TBL011` a LEFT JOIN ( SELECT * FROM `TBL007-A` UNION ALL SELECT * FROM `TBL007-B` UNION ALL SELECT * FROM `TBL007-C` ) b ON a.PN_MDL_NO = b.PN_MDL_NO WHERE a.PN_CARR LIKE CONCAT('%', #{deviceCarr}, '%') /* 기기 통신사 */
and a.PN_MKR NOT IN ('SAM','LGE','APP')
and a.PN_MKR = #{pnMkr}
AND a.PN_NET_TYPE = #{selectedNetType}
AND a.PN_MDL_NM LIKE CONCAT('%', #{searchKey}, '%')
group by a.PN_MDL_NO, a.PN_STOR order by sellStatus desc, a.REL_DT desc, a.PN_MDL_NO, convert(a.PN_STOR, decimal), convert(a.PN_MSRP, decimal) desc

mapper.update:
update `TBL007-A`
set SELL_YN = #{sellYn},
PN_OFCL_SUBSD = #{pnOfclSubsd},
PN_EXT_SUBSD_DV = #{pnExtSubsdDv},
PN_EXT_SUBSD_RT = #{pnExtSubsdRt},
PN_EXT_SERV_YN = #{pnExtServYn},
PN_ETC = #{pnEtc},
AMD_DT = sysdate(),
AMD_MBR_ID =#{bnMbrId}
where BN_NO=(SELECT cf_get_bn_no(#{bnMbrId}))
and PN_MDL_NO=#{pnMdlNo}
and PN_STOR=#{pnStor}
and PN_MNT_RT_NO = #{pnMntRtNo}
and MNT_CARR = #{mntCarr}

