mapper.select:
select B.MNT_USEYN_NO as MNT_USEYN_NO, /*사용여부 확인을 위한 idx*/ A.PN_MNT_RT_NO, /*요금제 번호 */ A.PN_MNT_RT_NM, A.PN_NET_TYPE, A.PN_MNT_AMT, A.PN_MNT_DATA, A.PN_MNT_CALL, A.PN_MNT_SMS, (SELECT AMD_MBR_ID FROM TBL015 t WHERE t.BN_NO =(SELECT cf_get_bn_no(#{bnMbrId})) AND A.PN_MNT_RT_NO=t.PN_MNT_RT_NO ORDER by INP_DT desc LIMIT 0,1) as AMD_MBR_ID, (SELECT MBR_NM FROM TBL002 tt002 WHERE tt002.BN_MBR_ID = (SELECT AMD_MBR_ID FROM TBL015 t WHERE t.BN_NO =(SELECT cf_get_bn_no(#{bnMbrId})) AND A.PN_MNT_RT_NO=t.PN_MNT_RT_NO ORDER by INP_DT desc LIMIT 0,1)) AS AMD_MBR_NM, DATE_FORMAT((SELECT INP_DT FROM TBL015 t WHERE t.BN_NO =(SELECT cf_get_bn_no(#{bnMbrId})) AND A.PN_MNT_RT_NO=t.PN_MNT_RT_NO ORDER by INP_DT desc LIMIT 0,1),'%Y-%m-%d %H:%i') as AMD_DT from TBL010 A left join (select * from TBL013 where BN_NO=(SELECT cf_get_bn_no(#{bnMbrId}))) B on A.PN_MNT_RT_NO = B.PN_MNT_RT_NO WHERE A.pn_carr = #{carr}
AND B.MNT_USEYN_NO is not null
AND B.MNT_USEYN_NO is null
AND pn_net_type = #{selectedNetType}
order by MNT_USEYN_NO is null asc, PN_NET_TYPE desc, convert(PN_MNT_AMT, decimal) DESC

mapper.delete:
delete from TBL013
where PN_MNT_RT_NO = #{pnMntRtNo}
and BN_NO = (SELECT cf_get_bn_no(#{bnMbrId}))

mapper.select:
SELECT ( SELECT bb1.SELL_YN FROM ( SELECT SELL_COND_NO, PN_MDL_NO, PN_STOR, BN_NO, MNT_CARR, SELL_YN FROM `TBL007-A` UNION ALL SELECT SELL_COND_NO, PN_MDL_NO, PN_STOR, BN_NO, MNT_CARR, SELL_YN FROM `TBL007-B` UNION ALL SELECT SELL_COND_NO, PN_MDL_NO, PN_STOR, BN_NO, MNT_CARR, SELL_YN FROM `TBL007-C` ) bb1 WHERE a.PN_MDL_NO = bb1.PN_MDL_NO AND a.PN_STOR = bb1.PN_STOR AND bb1.SELL_YN = 'y' AND bb1.BN_NO = (SELECT cf_get_bn_no(#{bnMbrId})) AND bb1.MNT_CARR = #{mntCarr} GROUP BY bb1.SELL_YN ) AS 'sellStatus', DATE_FORMAT(( SELECT MAX(AMD_DT) FROM ( SELECT PN_MDL_NO, PN_STOR, BN_NO, MNT_CARR, AMD_DT FROM `TBL007-A` UNION ALL SELECT PN_MDL_NO, PN_STOR, BN_NO, MNT_CARR, AMD_DT FROM `TBL007-B` UNION ALL SELECT PN_MDL_NO, PN_STOR, BN_NO, MNT_CARR, AMD_DT FROM `TBL007-C` ) bb2 WHERE a.PN_MDL_NO = bb2.PN_MDL_NO AND a.PN_STOR = bb2.PN_STOR AND bb2.BN_NO = (SELECT cf_get_bn_no(#{bnMbrId})) AND bb2.MNT_CARR = #{mntCarr} ), '%Y-%m-%d %H:%i') as 'lastModiDate', (SELECT MBR_NM FROM TBL002 tt002 WHERE tt002.BN_MBR_ID = ( SELECT bb3.AMD_MBR_ID FROM ( SELECT PN_MDL_NO, PN_STOR, BN_NO, MNT_CARR, AMD_DT, AMD_MBR_ID FROM `TBL007-A` UNION ALL SELECT PN_MDL_NO, PN_STOR, BN_NO, MNT_CARR, AMD_DT, AMD_MBR_ID FROM `TBL007-B` UNION ALL SELECT PN_MDL_NO, PN_STOR, BN_NO, MNT_CARR, AMD_DT, AMD_MBR_ID FROM `TBL007-C` ) bb3 WHERE a.PN_MDL_NO = bb3.PN_MDL_NO AND a.PN_STOR = bb3.PN_STOR AND bb3.BN_NO = (SELECT cf_get_bn_no(#{bnMbrId})) AND bb3.MNT_CARR = #{mntCarr} ORDER BY bb3.AMD_DT DESC LIMIT 0, 1 )) as 'lastModiMbrNm', ( SELECT bb4.AMD_MBR_ID FROM ( SELECT PN_MDL_NO, PN_STOR, BN_NO, MNT_CARR, AMD_DT, AMD_MBR_ID FROM `TBL007-A` UNION ALL SELECT PN_MDL_NO, PN_STOR, BN_NO, MNT_CARR, AMD_DT, AMD_MBR_ID FROM `TBL007-B` UNION ALL SELECT PN_MDL_NO, PN_STOR, BN_NO, MNT_CARR, AMD_DT, AMD_MBR_ID FROM `TBL007-C` ) bb4 WHERE a.PN_MDL_NO = bb4.PN_MDL_NO AND a.PN_STOR = bb4.PN_STOR AND bb4.BN_NO = (SELECT cf_get_bn_no(#{bnMbrId})) AND bb4.MNT_CARR = #{mntCarr} ORDER BY bb4.AMD_DT DESC LIMIT 0, 1 ) AS 'lastModiMbr', a.PN_MKR, a.PN_MDL_NM, a.PN_MSRP, a.PN_NET_TYPE, a.PN_STOR, a.PN_MDL_NO, a.REL_DT FROM `TBL011` a LEFT JOIN ( SELECT PN_MDL_NO FROM `TBL007-A` UNION ALL SELECT PN_MDL_NO FROM `TBL007-B` UNION ALL SELECT PN_MDL_NO FROM `TBL007-C` ) b ON a.PN_MDL_NO = b.PN_MDL_NO WHERE a.PN_CARR LIKE CONCAT('%', #{deviceCarr}, '%') /* 기기 통신사 */
and a.PN_MKR NOT IN ('SAM','LGE','APP')
and a.PN_MKR = #{pnMkr}
AND a.PN_NET_TYPE = #{selectedNetType}
AND a.PN_MDL_NM LIKE CONCAT('%', #{searchKey}, '%')
group by a.PN_MDL_NO, a.PN_STOR order by sellStatus desc, a.REL_DT desc, a.PN_MDL_NO, convert(a.PN_STOR, decimal), convert(a.PN_MSRP, decimal) desc

mapper.update:
