mapper.update:
UPDATE TBL002
    SET PWD_NO = #{tempPwd}
        ,PWNO_INIT_YN = 'Y'
        ,PWERR_CNT = '0'
        ,LGIN_AVL_YN = 'Y'
        ,AMD_DT = CURRENT_TIMESTAMP
WHERE BN_MBR_ID = #{mstMbrId}
    AND BN_NO = (
        SELECT BN_NO
        FROM TBL003
        WHERE BN_REG_NO = #{bnRegNo}
            AND OWNER_NM = #{ownerNm}
            AND MST_MBR_ID = #{mstMbrId}
    )
    AND MST_MBR_YN = 'Y'

mapper.insert:
INSERT INTO TBL003 (
    BN_NO
    ,BN_REG_NO
    ,BN_NM
    ,OWNER_NM
    ,BN_REG_CARD
    ,BN_ADDR_SIDO
    ,BN_ADDR_SIGG
    ,BN_ADDR_DTL
    ,MST_MBR_ID
    ,BN_PN_NO
    ,BN_IMG
    ,MKT_YN
) VALUES (
    (SELECT CAST(
        (SELECT(
            IFNULL(
                (SELECT MAX(BN_NO) FROM TBL003 B WHERE BN_NO LIKE CONCAT(
                    (SELECT DATE_FORMAT(CURRENT_TIMESTAMP, '%Y%m%d')), '%'
                )), CONCAT(
                    (SELECT DATE_FORMAT(CURRENT_TIMESTAMP, '%Y%m%d')), '0000'
                )
            ) + 1
        ))
    AS CHAR))
    ,#{bnRegNo}
    ,#{bnNm}
    ,#{ownerNm}
    ,#{bnRegCard}
    ,#{bnAddrSido}
    ,#{bnAddrSigg}
    ,#{bnAddrDtl}
    ,#{mstMbrId}
    ,#{bnPnNo}
    ,#{bnImg}
    ,#{mktYn}
)

mapper.update:
UPDATE TBL002
SET PWD_NO = #{pwdNo}
    ,PWNO_INIT_YN = 'N'
    ,AMD_DT = CURRENT_TIMESTAMP
    ,AUTH_DT = CURRENT_TIMESTAMP
WHERE BN_MBR_ID = #{bnMbrId}
