mapper.select:
select T.CALL_ST_CD, t1.MNT_CARR, t1.PN_REG_DIS, (select PN_MNT_RT_NM FROM TBL010 tt1 where tt1.PN_MNT_RT_NO = t1.PN_MNT_RT_NO) as PN_MNT_RT_NM, (select PN_NET_TYPE FROM TBL010 tt1 where tt1.PN_MNT_RT_NO = t1.PN_MNT_RT_NO) as PN_NET_TYPE, t1.PN_MNT_RT_NO, t1.PN_MDL_NO, (select PN_MDL_NM FROM TBL011 tt2 where tt2.PN_MDL_NO = t1.PN_MDL_NO) as PN_MDL_NM, t1.PN_STOR, (select PN_MSRP FROM TBL011 tt4 where tt4.PN_MDL_NO = t1.PN_MDL_NO) as PN_MSRP, (SELECT MBR_NM FROM TBL001 tt3 where tt3.CL_MBR_ID = t1.CL_MBR_ID) as CL_MBR_NM, (SELECT PN_MKR FROM TBL011 pnmkr where pnmkr.PN_MDL_NO = t1.PN_MDL_NO) as PN_MKR, DATE_FORMAT(T.VISIT_DT, '%Y-%m-%d %H:%i') as VISIT_DT, t1.DEAL_REQ, T.MBR_PN_NO, T.OLD_OFCL_SUBSD, T.OLD_EXT_SUBSD_DV, T.OLD_EXT_SUBSD_RT, T.OLD_EXT_SERV_YN, T.CALL_NO, T.CALL_CONT, T.BN_MBR_ID, (SELECT MBR_NM FROM TBL002 tt5 WHERE tt5.BN_MBR_ID = T.BN_MBR_ID) as MBR_NM, DATE_FORMAT(T.INP_DT, '%Y-%m-%d %H:%i') as INP_DT, DATE_FORMAT(T.AMD_DT, '%Y-%m-%d %H:%i') as AMD_DT from TBL008 T left join TBL006 t1 on T.DEAL_NO = t1.DEAL_NO where T.BN_NO = (SELECT cf_get_bn_no(#{loginMbrId}))
and T.CALL_ST_CD = #{callStCd}
and DATE_FORMAT(T.VISIT_DT, '%Y-%m-%d') = #{visitDt}
and t1.MNT_CARR = #{mntCarr}
and t1.PN_REG_DIS = #{counselType}
and (SELECT MBR_NM FROM TBL001 tt5 where tt5.CL_MBR_ID = t1.CL_MBR_ID) LIKE CONCAT('%', #{clientNm}, '%')
and T.BN_MBR_ID = #{bnMbr}
and DATE_FORMAT(T.AMD_DT, '%Y-%m-%d') = #{searchDay}

SELECT MKT_YN FROM TBL001 WHERE CL_MBR_ID = (
    SELECT CL_MBR_ID FROM TBL006 WHERE DEAL_NO = (
        SELECT DEAL_NO FROM TBL008 WHERE CALL_NO = #{callNo}
    )
)

SELECT A.DEAL_NO ,A.MBR_NM ,A.MBR_PN_NO ,DATE_FORMAT(A.PN_REG_DT, '%Y-%m-%d') AS PN_REG_DT ,( SELECT CASE WHEN B.MKT_RSLT = 'S' THEN '성공' WHEN B.MKT_RSLT = 'F' THEN '실패' ELSE '미진행' END MKT_RSLT_TXT FROM TBL009 B WHERE B.DEAL_NO = A.DEAL_NO ) AS MKT_RSLT_TXT ,DATE_FORMAT(A.AMD_DT, '%Y-%m-%d %H:%i') AS AMD_DT ,C.REG_CARR AS MNT_CARR ,( SELECT DISTINCT D.PN_MDL_NM FROM TBL011 D WHERE D.PN_MDL_NO = C.REG_PN_MDL_NO ) AS PN_MDL_NM FROM TBL009 A, TBL008 C WHERE A.BN_NO = (SELECT cf_get_bn_no(#{bnMbrId})) AND A.DEAL_NO = C.DEAL_NO
AND A.MKT_RSLT = #{srchType}
AND A.MBR_NM LIKE CONCAT('%', #{srchName}, '%') AND PN_REG_DT LIKE CONCAT(#{srchDate}, '%')

SELECT DISTINCT PN_MKR
FROM TBL011
WHERE PN_NET_TYPE = #{pnNetType}

SELECT DISTINCT PN_MKR
FROM TBL001
WHERE PN_DML_NM = #{pnMdlNm}

mapper.insert:
UPDATE TBL009
SET MKT_RSLT = #{mktRslt}
,VISIT_DT = #{visitDt}
,PN_CARR = #{pnCarr}
,PN_REG_DIS = #{pnRegDis}
,PN_MNT_RT_NO = #{pnMntRtNo}
,PN_MDL = #{pnMdl}
,MKT_CMNT = #{mktCmnt}
,AMD_DT = CURRENT_TIMESTAMP
WHERE DEAL_NO = #{dealNo}
