mapper.update:
UPDATE TBL003
SET BN_REG_NO = #{bnRegNo}
    ,BN_NM = #{bnNm}
    ,OWNER_NM = #{ownerNm}
    ,BN_REG_CARD = #{bnRegCard}
    ,BN_ADDR_SIDO = #{bnAddrSido}
    ,BN_ADDR_SIGG = #{bnAddrSigg}
    ,BN_ADDR_DTL = #{bnAddrDtl}
    ,CARD_CHK_YN = #{cardChkYn}
    ,BN_PN_NO = #{bnPnNo}
    ,BN_IMG = #{bnImg}
    ,AMD_DT = CURRENT_TIMESTAMP()
WHERE
    BN_NO = #{bnNo}

mapper.insert:
INSERT INTO TBL002 (
    BN_MBR_ID
    ,PWD_NO
    ,PWNO_INIT_YN
    ,PWERR_CNT
    ,LGIN_AVL_YN
    ,BN_NO
    ,MBR_NM
    ,MST_MBR_YN
    ,SELLER_PRMS
    ,ACTV_YN
    ,AMD_MBR_ID
) VALUES (
    #{bnMbrId}
    ,#{pwdNo}
    ,'Y'
    ,'0'
    ,'Y'
    ,(SELECT cf_get_bn_no(#{amdMbrId}))
    ,#{mbrNm}
    ,#{mstMbrYn}
    ,#{sellerPrms}
    ,'Y'
    ,#{amdMbrId}
)

mapper.update:
UPDATE TBL002
SET ACTV_YN = 'N'
    ,AMD_DT = CURRENT_TIMESTAMP
    ,AMD_MBR_ID = #{amdMbrId}
WHERE BN_MBR_ID = #{bnMbrId}

UPDATE TBL002
SET PWD_NO = #{pwdNo}
    ,PWNO_INIT_YN = 'Y'
    ,PWERR_CNT = 0
    ,LGIN_AVL_YN = 'Y'
    ,AMD_DT = CURRENT_TIMESTAMP
    ,AMD_MBR_ID = #{amdMbrId}
WHERE BN_MBR_ID = #{bnMbrId}

UPDATE TBL002
SET SELLER_PRMS = #{sellerPrms}
    ,AMD_DT = CURRENT_TIMESTAMP
    ,AMD_MBR_ID = #{amdMbrId}
WHERE BN_MBR_ID = #{bnMbrId}

mapper.insert:
INSERT INTO TBL012 (
    BRD_NO
    ,BN_NO
    ,BRD_TITLE
    ,BRD_CONT
    ,BRD_ATT_FILE_1
    ,BRD_ATT_FILE_2
    ,BRD_WRITER
) VALUES (
    IFNULL((SELECT MAX(BRD_NO) FROM TBL012 B WHERE BN_NO = (SELECT cf_get_bn_no(#{bnMbrId}))), 0) + 1
    ,(SELECT cf_get_bn_no(#{bnMbrId}))
    ,#{brdTitle}
    ,#{brdCont}
    ,#{brdAttFile1}
    ,#{brdAttFile2}
    ,#{bnMbrId}
)

mapper.update:
UPDATE TBL012
SET BRD_TITLE = #{brdTitle}
    ,SHOW_YN = #{showYn}
    ,BRD_REG_DT = CURRENT_TIMESTAMP
    ,BRD_ATT_FILE_1 = #{brdAttFile1}
    ,BRD_ATT_FILE_2 = #{brdAttFile2}
    ,BRD_CONT = #{brdCont}
    ,AMD_DT = CURRENT_TIMESTAMP
WHERE
    BN_NO = (
        SELECT cf_get_bn_no(#{bnMbrId})
    )
    AND BRD_NO = #{brdNo}
